mapper.update:

	SELECT a.hobby_nm
	FROM a.hobby
	WHERE 

UPDATE public.user AS a.user
INNER JOIN user_hobby AS b.user_hobby
	SET user_id=#{userId}
		, user_nm=#{userNm}
		, user_eml_addr=#{userEmlAddr}
		, user_dept_no=#{userDeptNo}
		, user_telno=#{userTelno}
		, user_addr=#{userAddr}
		, user_aprv_yn=#{userAprvYn}
where user_no=#{userNo}
